describe ClassResourcesController do

  # This should return the minimal set of attributes required to create a valid
  # ClassResource. As you add validations to ClassResource, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all class_resources as @class_resources" do
      class_resource = ClassResource.create! valid_attributes
      get :index
      assigns(:class_resources).should eq([class_resource])
    end
  end

  describe "GET show" do
    it "assigns the requested class_resource as @class_resource" do
      class_resource = ClassResource.create! valid_attributes
      get :show, :id => class_resource.id.to_s
      assigns(:class_resource).should eq(class_resource)
