  end

  describe "GET new" do
    it "assigns a new class_resource as @class_resource" do
      get :new
      assigns(:class_resource).should be_a_new(ClassResource)
    end
  end

  describe "GET edit" do
    it "assigns the requested class_resource as @class_resource" do
      class_resource = ClassResource.create! valid_attributes
      get :edit, :id => class_resource.id.to_s
      assigns(:class_resource).should eq(class_resource)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new ClassResource" do
        expect {
          post :create, :class_resource => valid_attributes
        }.to change(ClassResource, :count).by(1)
      end

      it "assigns a newly created class_resource as @class_resource" do
        post :create, :class_resource => valid_attributes
        assigns(:class_resource).should be_a(ClassResource)
        assigns(:class_resource).should be_persisted
      end

      it "redirects to the created class_resource" do
        post :create, :class_resource => valid_attributes
        response.should redirect_to(ClassResource.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved class_resource as @class_resource" do
