        assigns(:class_resource).should be_a_new(ClassResource)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        ClassResource.any_instance.stub(:save).and_return(false)
        post :create, :class_resource => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested class_resource" do
        class_resource = ClassResource.create! valid_attributes
        # Assuming there are no other class_resources in the database, this
